package com.javaclass007;

public class IncrementDicrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10;
		num=num+1;//=num++
		num+=1;
		num++;
		System.out.println(num);
		//increment operator add 1 to a variable
		
		num++;
		System.out.println(num);
		//10++ ; Invalid argumument to a variable
		
		num--;
		num--;
		num--;
		System.out.println(num);
	}

}
